import React from 'react';
import Button from './common/Button';

const HeroSection = () => {
    return (
        <section className="flex flex-col md:flex-row items-center justify-between bg-gradient-to-r from-blue-500 to-purple-500 rounded-lg p-8">
            <div className="md:w-1/2">
                <h1 className="text-4xl font-bold text-white mb-4">Welcome to Jadixpert</h1>
                <p className="text-lg text-white mb-6">Your one-stop solution for expert advice and services.</p>
                <Button label="Get Started" onClick={() => alert('Button Clicked!')} />
                <div className="mt-4">
                    <span className="inline-block bg-white text-blue-500 rounded-full px-3 py-1 text-sm font-semibold mr-2">Badge 1</span>
                    <span className="inline-block bg-white text-blue-500 rounded-full px-3 py-1 text-sm font-semibold">Badge 2</span>
                </div>
            </div>
            <div className="md:w-1/2 mt-6 md:mt-0">
                <img src="https://via.placeholder.com/400" alt="Hero" className="rounded-lg shadow-lg" />
            </div>
        </section>
    );
};

export default HeroSection;
